import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Favorites.css';

const Favorites = () => {
    const [favorites, setFavorites] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const savedFavorites = JSON.parse(localStorage.getItem("favorites")) || [];
        setFavorites(savedFavorites);
    }, []);

    const navigateToBook = (info) => {
        navigate(`/book/${info.id}`, { state: { id: info.id } });
    };

    return (
        <div className="favorites-container">
            <h1>Your Favorite Books</h1>
            <div className='wraper'>
                {
                    favorites.length > 0 ? favorites.map((info, index) => (
                        <div key={index} className="book-item" onClick={() => navigateToBook(info)}>
                            <img src={info.imageUrl} alt={info.bookName} />
                            <h1>{info.bookName}</h1>
                        </div>
                    )) : <p>No favorite books yet!</p>
                }
            </div>
        </div>
    );
};

export default Favorites;
