// src/CarouselComponent.js
import React from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css'; // Import the carousel styles
import './carouselComponent.css'; // Custom styles if you have any
import infoArr from '../../infoArr'; // Make sure the path to infoArr is correct

const CarouselComponent = () => {
    return (
        <Carousel
            showArrows={true}
            autoPlay={true} // Automatically start the carousel
            infiniteLoop={true} // Loop back to the first image after the last image
            showThumbs={false} // Hide thumbnails
            showStatus={false} // Hide the status of the current slide
            emulateTouch={true} // Allow swipe functionality
            interval={3000} // Change to the next image every 3 seconds
            stopOnHover={true} // Stop changing on hover
            swipeable={true} // Allow swiping
        >
            {infoArr.map(book => (
                <div key={book.id}>
                    <img src={book.imageUrl} alt={book.bookName} />
                    <p className="legend">{book.bookName}</p> // Optionally show the book name as a caption
                </div>
            ))}
        </Carousel>
    );
};

export default CarouselComponent;
