// src/CarouselComponent.js
import React from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css'; // Import the styles
import './carouselComponent.css';
import infoArr from "../../"
const CarouselComponent = () => {
    return (
        <Carousel
            showArrows={true}
            autoPlay={true}
            infiniteLoop={true}
            showThumbs={false}
            showStatus={false}
            emulateTouch={true}
            interval={3000}
            stopOnHover={true}
            swipeable={true}
            dynamicHeight={true}
        >
            <div>
                <img src="https://via.placeholder.com/1200x600?text=Beautiful+View" alt="Beautiful View" />
            </div>
            <div>
                <img src="https://via.placeholder.com/1200x600?text=City+Lights" alt="City Lights" />
            </div>
            <div>
                <img src="https://via.placeholder.com/1200x600?text=Mountain+Peak" alt="Mountain Peak" />
            </div>
        </Carousel>
    );
};

export default CarouselComponent;
