import React, { useState } from 'react';
import infoArr from "../../infoArr";
import { useNavigate } from 'react-router-dom';
import './Booklist.css';
import Footer from '../Footer/Footer';

const Booklist = () => {
    const [searchTerm, setSearchTerm] = useState("");
    const [books, setBooks] = useState(infoArr); // State to hold books with favorite status
    const [favoriteBooks, setFavoriteBooks] = useState([]); // State to hold favorite books
    const navigat = useNavigate();

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value);
    };

    // Function to toggle favorite status
    const toggleFavorite = (id) => {
        const updatedBooks = books.map(book =>
            book.id === id ? { ...book, isFavorite: !book.isFavorite } : book
        );
        setBooks(updatedBooks);
    };

    // Function to add/remove book from favorites
    const addToFavorites = (id) => {
        const updatedBooks = books.map(book =>
            book.id === id ? { ...book, isFavorite: !book.isFavorite } : book
        );
        setBooks(updatedBooks);
        const updatedFavoriteBooks = updatedBooks.filter(book => book.isFavorite);
        setFavoriteBooks(updatedFavoriteBooks);
    };

    const navigateToBook = (info) => {
        navigat(info.id, { state: { id: info.id } });
    };

    return (
        <div className="booklist-container">
            <div className='searchDiv'>
                <input
                    type="text"
                    placeholder="Search Books"
                    value={searchTerm}
                    onChange={handleSearchChange}
                    className="booklist-input"
                />
            </div>
            <div className='wraper'>
                {
                    books.map((info, index) => (
                        <div key={index} className="book-item" onClick={() => navigateToBook(info)}>
                            <img src={info.imageUrl} alt={info.bookName} />
                            <h1>{info.bookName}</h1>
                            <button onClick={() => toggleFavorite(info.id)}>
                                {info.isFavorite ? "Remove from Favorites" : "Add to Favorites"}
                            </button>
                        </div>
                    ))
                }
            </div>
            <Footer searchTerm={searchTerm} />

            <div className="favorite-books-container">
                <h2>Favorite Books</h2>
                {
                    favoriteBooks.map((info, index) => (
                        <div key={index} className="favorite-book-item">
                            <img src={info.imageUrl} alt={info.bookName} />
                            <h1>{info.bookName}</h1>
                        </div>
                    ))
                }
            </div>
        </div>
    );
};

export default Booklist;
