// src/CarouselComponent.js
import React from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css'; // Import the styles
import './carouselComponent.css';
import infoArr from "../../infoArr"
const CarouselComponent = () => {
    return (
        <Carousel
            showArrows={true}
            autoPlay={true}
            infiniteLoop={true}
            showThumbs={false}
            showStatus={false}
            emulateTouch={true}
            interval={3000}
            stopOnHover={true}
            swipeable={true}
            dynamicHeight={true}
        >
            <div>
                <img src="https://via.placeholder.com/1200x600?text=Beautiful+View" alt="Beautiful View" />
                {
                    infoArr.map((el) => {
                        return (
                            <>
                                <img src={el.imageUrl} />
                            </>
                        )
                    })
                }
            </div>

        </Carousel>
    );
};

export default CarouselComponent;
