import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom'; // Make sure to import Routes

import infoArr from './infoArr';

// Main Screen Component
const MainScreen = () => {
  return (
    <div>
      <h1>Library</h1>
      {infoArr.map((book, index) => (
        <div key={index}>
          <Link to={`/details/${index}`}>
            <img src={book.imageUrl} alt={book.bookName} />
            <h2>{book.bookName}</h2>
          </Link>
        </div>
      ))}
    </div>
  );
};

// Details Page Component
const DetailsPage = () => {
  return (
    <div>
      <h1>Years</h1>
      <ul>
        {infoArr.map((book, index) => (
          <li key={index}>{book.date.slice(0, 4)}</li>
        ))}
      </ul>
    </div>
  );
};

// App Component
const App = () => {
  return (
    <Router>
      <Routes>
        <Route exact path="/" element={<MainScreen />} />
        <Route path="/details/:id" element={<DetailsPage />} />
      </Routes>
    </Router>
  );
};

export default App;
