import React from 'react';

const BookList = ({ searchTerm, handleSearchChange, filteredBooks = [], navigateToBook }) => {
    return (
        <div className="booklist-container">
            <input
                type="text"
                placeholder="Search Books"
                value={searchTerm}
                onChange={handleSearchChange}
                className="booklist-input"
            />
            <div className='sss'>
                {
                    filteredBooks.map((info, index) => (
                        <div key={index} className="book-item" onClick={() => navigateToBook(info)}>
                            <img src={info.imageUrl} alt={info.bookName} />
                            <h1>{info.bookName}</h1>
                        </div>
                    ))
                }
            </div>
        </div>
    );
}

export default BookList;
