// src/CarouselComponent.js
import React from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css'; // Import the styles
import './carouselComponent.css';
import infoArr from "../../infoArr";  // Make sure the path is correct

const CarouselComponent = () => {
    return (
        <Carousel
            showArrows={true}
            autoPlay={true}
            infiniteLoop={true}
            showThumbs={false}
            showStatus={false}
            emulateTouch={true}
            interval={10000}
            stopOnHover={true}
            swipeable={true}
        >
            {infoArr.map((item) => (
                <div key={item.id}>
                    <img src={item.imageUrl} alt={item.bookName} />
                </div>
            ))}
        </Carousel>
    );
};

export default CarouselComponent;
