import React, { useState } from 'react';
import infoArr from "../../infoArr";
import { useNavigate } from 'react-router-dom';
import './Booklist.css';
import Footer from '../Footer/Footer';
import Popup from '../Popup/Popup'; // Import the Popup component

const Booklist = () => {
    const [searchTerm, setSearchTerm] = useState("");
    const [selectedBook, setSelectedBook] = useState(null); // State to track the selected book
    const navigat = useNavigate();

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value);
    };

    const filteredBooks = infoArr.filter(info =>
        info.bookName.toLowerCase().includes(searchTerm.toLowerCase())
    );

    const navigateToBook = (info) => {
        setSelectedBook(info); // Set the selected book
    };

    const closePopup = () => {
        setSelectedBook(null); // Reset selected book to close the popup
    };

    return (
        <div className="booklist-container">
            <div className='searchDiv'>
                <input
                    type="text"
                    placeholder="Search Books"
                    value={searchTerm}
                    onChange={handleSearchChange}
                    className="booklist-input"
                />
            </div>
            <div className='wraper'>
                {
                    filteredBooks.map((info, index) => (
                        <div key={index} className="book-item" onClick={() => navigateToBook(info)}>
                            <img src={info.imageUrl} alt={info.bookName} />
                            <h1>{info.bookName}</h1>
                        </div>
                    ))
                }
            </div>
            <Footer searchTerm={searchTerm} />
            {selectedBook && <Popup book={selectedBook} onClose={closePopup} />} {/* Render the Popup component */}
        </div>
    );
};

export default Booklist;
