// src/components/BookDetail.js
import React from 'react';
import { useParams } from 'react-router-dom';
import infoArr from ".././infoArr"
// import './BookDetail.css';  // Corrected path

const BookDetail = () => {
    const { id } = useParams();
    const book = infoArr[id];

    if (!book) {
        return <div>Book not found</div>;
    }

    return (
        <div className="book-detail">
            <img src={book.imageUrl} alt={book.bookName} className="book-detail-image" />
            <h2>{book.bookName}</h2>
            <h3>by {book.author}</h3>
            <p>{book.description}</p>
            <a href={book.readBook} target="_blank" rel="noopener noreferrer">Read Book</a>
            <p>Published Date: {book.date}</p>
        </div>
    );
};

export default BookDetail;
